import { Box } from '@mui/material';
import React, {useState,useEffect} from 'react';
import { useParams } from 'react-router-dom';
import Service from '../Service';

export default function SenderMail() {

    const [nomd, setNomd] = useState('');
    const [prenomd, setPrenomd] = useState('');
    const [emaild, setEmaild] = useState('');
    const {id} = useParams();
    const idp = Number(id);

    const sender = {nomd,prenomd,emaild}

    useEffect(() => {
      getPersonneInformation(idp);
    },[]);
    
    const getPersonneInformation = () =>{
        if(idp){
            Service.editById(idp)
            .then( response =>{
                setNomd(response.data.nom);
                setPrenomd(response.data.prenom);
                setEmaild(response.data.email);
            })
            .catch(error => {
                console.log("Il y a erreur lors du chargement des données pour mail : ",error);
            })   
        }
    }

  return (
      <Box>
          <p> Boite de sender {nom} , {prenom}, {email} </p>
      </Box>
  );
}
