import React, {useState,useEffect} from 'react';
import { Button, Container, Link, TextField } from '@mui/material';
import { Box } from '@mui/system';
import { useNavigate, useParams } from 'react-router-dom';
import Service from '../Service';
import NotFound from '../NotFound';

export default function Personne() {

  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [nomUpdated, setNomUpdated] = useState('');
  const [prenomUpdated, setPrenomUpdated] = useState('');
  const home = useNavigate();
  const {id} = useParams();
  const idp = Number(id);


  const personneReceive = {nom,prenom,idp};
  const personneUpdated = {nomUpdated, prenomUpdated, idp};

  useEffect(() => {
    getPersonne(idp);
  }, []);
  
  const getPersonne = () => {
    Service.getById(idp)
    .then(response =>{
      setNom(response.data.nom);
      setPrenom(response.data.prenom);
    })
    .catch(error => {
      console.log("There is a error when personne details (Service.getById())",error);
    })
  }

  const updatePersonne = (e) => {
    if(idp){
      Service.editById(idp,personneUpdated)
      .then(
        console.log("Personne updated to : ", personneUpdated) ,
        home('/') 
      )
      .catch(error => {
        console.log("Update function doesn't work : ",error);
      })
    }
    else{
      return (
        <NotFound/>
      )
    }
  }

  return (
    <React.Fragment>
    <Container>
      <h1> Update a Personne </h1> 
      <Box component="form">
          < div>
              <p key={idp}></p>
              <TextField
              id="Nom"
              label={nom}
              value={nomUpdated}
              placeholder='Nom de la personne'
              onChange = {(e)=>SVGAnimatedTransformList(e.target.value)}
              />
               <TextField
              id="Prenom"
              label={prenom}
              value={prenomUpdated}
              placeholder='Prenom de la personne'
              onChange = {(e)=>setPrenom(e.target.value)}
              />
              <hr/>
              <Button variant="outlined" onClick={((e)=>{updatePersonne(idp,personneUpdated)})} > Sauvegarder </Button>
            </div>  
      </Box>  
      <hr/>
      <Link href={'/'}> Retour Ã  l'acceuil </Link>
    </Container>  
  </React.Fragment>
  );

}
