import { Button, Container, Link, TextField } from '@mui/material';
import { Box } from '@mui/system';
import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Service from '../Service';

export default function Create() {

    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const personne = {nom , prenom};

    const home = useNavigate();

    const savePerson = (e) => {
        e.preventDefault();
        Service.savePerson(personne)
        .then(response =>{
            home('/');
        })
        .catch(error => {
            console.log("Oups, il y erreurs : ", error);
    }) 

    }

  return (
      <React.Fragment>
          <Container>
            <h1> Create a person </h1> 
            <Box component="form">
                <div>
                    <TextField
                    id="Nom"
                    label="Nom"
                    placeholder='Mettre le nom de la personne'
                    />
                     <TextField
                    id="Prénom"
                    label="Prénom"
                    placeholder='Mettre le prenom de la personne'
                    />
                    <hr/>
                    <Button variant="outlined"> Sauvegarder </Button>
                </div>
            </Box>
            <hr/>
            <Link href={'/'}> Retour à l'acceuil </Link>
          </Container>
          
      </React.Fragment>
  );

}
