import React, {useState,useEffect} from 'react';
import { Button, Container, Link, TextField } from '@mui/material';
import { Box } from '@mui/system';
import { useParams } from 'react-router-dom';
import Service from '../Service';

export default function Personne() {

  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const personneReceive = {nom,prenom,idp};
  const [nomUpdated, setNomUpdated] = useState('');
  const [prenomUpdated, setPrenomUpdated] = useState('');

  const {id} = useParams();
  const idp = Number(id) ;

  useEffect(() => {
    getPersonne(idp);
  }, []);
  

  const getPersonne = (idp) => {
    Service.getById(idp)
    .then(response =>{
      setNom(response.data.nom);
      setPrenom(response.data.prenom);
      console.log("Recupération DB de Personne", personneReceive);
    })
    .catch(error => {
      console.log("There is a error when personne details (Service.getById())",error);
    })
  }

  return (
    <React.Fragment>
    <Container>
      <h1> Update a Personne </h1> 
      <Box component="form">
          < div>
              <TextField
              id="Nom"
              label={nom}
              placeholder='Mettre le nom de la personne'
              />
               <TextField
              id="Prenom"
              label={prenom}
              placeholder='Mettre le prenom de la personne'
              />
              <hr/>
              <Button variant="outlined" > Sauvegarder </Button>
            </div>  
      </Box>  
      <hr/>
      <Link href={'/'}> Retour à l'acceuil </Link>
    </Container>  
  </React.Fragment>
  );

}
