import { Box } from '@mui/material';
import React, {useState,useEffect} from 'react';
import { useParams } from 'react-router-dom';
import Service from '../Service';

export default function SenderMail() {

    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [email, setEmail] = useState('');
    const {id} = useParams();
    const idp = Number(id);

    const sender = {nom,prenom,email}

    useEffect(() => {
      getPersonneInformation(idp);
    },[]);
    
    const getPersonneInformation = () =>{
        if(idp){
            Service.editById(idp)
            .then( response =>{
                setNom(response.data.nom);
                setPrenom(response.data.prenom);
                setEmail(response.data.email);
            })
            .catch(error => {
                console.log("Il y a erreur lors du chargement : ",error);
            })   
        }
    }

  return (
      <Box>
          <p> Boite de sender {nom} , {prenom}, {email} </p>
      </Box>
  );
}
