import { Button, Container, Link, TextField } from '@mui/material';
import { Box } from '@mui/system';
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Service from '../Service';

export default function Create() {

    const [nom, SetNom] = useState('');
    const [prenom, SetPrenom] = useState('');
    const {id} =useParams('');
    const idp = parseInt(id);

    const personne = {nom,prenom};

    const getPersonneId = (idp) => {
        Service.getById(idp,personne)
        .then( personne =>{
                console.log(personne.data);
                SetNom(personne.data.nom);
                SetPrenom(personne.data.prenom);
            }
        )
        .catch( 
            error => {
                console.log("Il y a eut une erreur : ", error)
            });
    }


  return (
      <React.Fragment>
          <Container>
            <h1> Create a person </h1> 
            <Box component="form">
                <div>
                    <TextField
                    id="Nom"
                    label="Nom"
                    />
                     <TextField
                    id="Prénom"
                    label="Prénom"
                    />
                    <hr/>
                    <Button variant="outlined"> Sauvegarder </Button>
                </div>
            </Box>
            <hr/>
            <Link href={'/'}> Retour à l'acceuil </Link>
          </Container>
          
      </React.Fragment>
  );

}
